    private void butSaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butSaveActionPerformed
        // TODO add your handling code here:
        String name = tfName.getText();
        String dob = tfDoB.getText();
        String address = tfAdrs.getText();
        String mobileNo = tfMobilNo.getText();
        String email = tfEml.getText();
        String accountNo = tfAccNumb.getText();
        String accountType = tfAccTyp.getText();
        String sortCode=tfSC.getText();
        String balance=tfBal.getText();
        String card=tfCard.getText();
   
        String p = addCustomer(name, dob, address, mobileNo, email, accountNo, accountType, sortCode, balance, card);
        if(p.equals("a")){
            this.setVisible(false);
            new CustomerPage().setVisible(true);                
        }
    }//GEN-LAST:event_butSaveActionPerformed

