    private static String addCustomer(String name,String dob,String address,String mobileNo,String email,String accountNo,String accountType,String sortCode,String balance,String card) {
        ws_customer.BanKCustomer_Service service = new ws_customer.BanKCustomer_Service();
        ws_customer.BanKCustomer port = service.getBanKCustomerPort();
        return port.editCustomer(name, dob, address, mobileNo, email, accountNo, accountType, sortCode, balance, card);
    }
